import React, { useEffect, useState } from 'react';
import { MetDetails } from './Matrimonyform';
import { del, list } from './PersonDetails';
import { Read } from './READ';

export const Online=()=>{

    const[localArr,setLocalArr]=useState([])
    const[visual,setVisual]=useState(false)
    const[study,setStudy]=useState(false)
    const[pos,setPos]=useState(0)
    const[edit,setEdit]=useState(false)

    const view=()=>{
        setLocalArr(list())
    }
    
    useEffect(()=>{
        view()
    },[])
    return(
        <>
        <div className="container">
        {
            (visual)?
            <>
                 <MetDetails/>

                <button onClick={
                    ()=>{
                        setVisual(false)
                    }
                }
                >
                Goback
                </button>
            </>
            :
            (edit)?
            <>
            <button onClick={
                ()=>{
                    setEdit(false)
                }
            }
            >
            Goback
            </button>
            </>
            :
            (study)?
            <>
            <Read who={pos}/>
            <button onClick={
                ()=>{
                    setStudy(false)
                }
            }
            
            >
                Go back
            </button>
            </>
            :
       
        <>

        <button onClick={
            ()=>{
                setVisual(true)
            }
        }
        >
            AddingNew
        </button>
        

            <div className="row justify-content-center">
                <div className="card bg-info text-light shadow-lg">
                <table className='table table-hover'>
                    <thead>
                        <tr>
                        <th> UserName</th>
                        <th> FirstName</th>
                        <th> Gender</th>
                        <th> Qualification</th>
                        <th> Age</th>
                        <th> Mblno</th>
                        <th> Email</th>
                        <th> Lang </th>
                        <th className='text-center'> Action</th>

                        
                        </tr>
                    </thead>
                    <tbody>
                        {localArr.map((elements,index)=>(
                                             
                        <tr>
                            <td>
                                
                                <button onClick={
                                    ()=>{
                                        setStudy(true)
                                        setPos(index)
                                    }
                                }
                                >
                                Read
                                </button>
                                
                                
                                
                                {elements.diaUname}</td>

                            <td>{elements.diaFname}</td>
                            <td>{elements.diaGen}</td>
                            <td>{elements.diaQuali}</td>
                            <td>{elements.diaAge}</td>
                            <td>{elements.diaMblno}</td>
                            <td>{elements.diaEmail}</td>
                            <td>{elements.diaLang}</td>
                            <td>
                               
                                <button className='btn btn-warning ms-3'
                                >
                                    Modify
                                </button>
                                <button className="btn btn-danger ms-3"

                                onClick={
                                    ()=>{
                                       del(index) 
                                    }
                                }
                                
                                >
                                    Discard
                                </button>
                            </td>

                            
                        </tr>
  ))}
                    </tbody>
            
                </table>
                </div>

            </div>
                            </>
        }
    
        </div>
        </>    
    )}
